mapper.select:
select a.filePath,a.zhxgsj,a.cjsj,a.fileType,b.ajlb_bm type from (
select wscflj filePath,zhxgsj,cjsj,'w' fileType,bmsah from yx_ws_ajjzwj
where cjsj >= #{startDate} and cjsj <= #{endDate}

    and ${dwbm}

order by cjsj
) a left join tyyw_gg_ajjbxx b on a.bmsah = b.bmsah

    where b.ajlb_bm in (${typeRestriction})

select a.filePath,a.zhxgsj,a.cjsj,a.fileType,b.ajlb_bm type from (
select wjlj||'\'||wjmc||wjhz||'.encry' filePath,zhxgsj,cjsj,'d' fileType,bmsah from yx_dzjz_jzmlwj
where cjsj >= #{startDate} and cjsj <= #{endDate}

    and ${dwbm}

order by cjsj
) a left join tyyw_gg_ajjbxx b on a.bmsah = b.bmsah

    where b.ajlb_bm in (${typeRestriction})

 CREATE OR REPLACE TRIGGER file_dzjz_update
AFTER INSERT OR UPDATE ON yx_dzjz_jzmlwj
FOR EACH ROW
DECLARE
MYTYPE varchar2(100);
BEGIN
 select ajlb_bm into MYTYPE from tyyw_gg_ajjbxx where bmsah = :NEW.BMSAH;
 INSERT INTO sjfh_file_newest(filePath,TYPE,fileType,ZHXGSJ,CJSJ) VALUES (:NEW.wjlj||'\'||:NEW.wjmc||:NEW.wjhz||'.encry',MYTYPE,'d',:NEW.ZHXGSJ,:NEW.CJSJ);
END;

CREATE OR REPLACE TRIGGER file_ws_update
AFTER INSERT OR UPDATE ON YX_WS_AJJZWJ
FOR EACH ROW
DECLARE
 MYTYPE varchar2(100);
BEGIN
 select ajlb_bm into MYTYPE from tyyw_gg_ajjbxx where bmsah = :NEW.BMSAH;
 INSERT INTO sjfh_file_newest(filePath,TYPE,fileType,ZHXGSJ,CJSJ) VALUES (:NEW.WSCFLJ,MYTYPE,'w',:NEW.ZHXGSJ,:NEW.CJSJ);
END;
