mapper.select:
 SELECT count(1)
  FROM yx_ws_ajjzwj f
INNER JOIN tyyw_gg_ajjbxx aj ON f.bmsah = aj.bmsah AND aj.sfsc = 'N'

       AND aj.ajlb_bm IN (${typeRestriction})


      AND ${ajdwbm}

 WHERE f.sfsc <> 'Y'

      AND ${fdwbm}

      AND f.zhxgsj >= #{startDate} AND f.zhxgsj <= #{endDate}

SELECT f.wscflj filePath, f.DWBM, f.zhxgsj, f.cjsj, 'w' fileType, aj.ajlb_bm type --,f.bmsah FROM yx_ws_ajjzwj f INNER JOIN tyyw_gg_ajjbxx aj ON f.bmsah = aj.bmsah AND aj.sfsc = 'N'
AND aj.ajlb_bm IN (${typeRestriction})
AND ${ajdwbm}
WHERE f.sfsc <> 'Y'
AND ${fdwbm}
AND f.zhxgsj >= #{startDate} AND f.zhxgsj <= #{endDate} ORDER BY f.zhxgsj

 SELECT count(1)
 FROM yx_dzjz_jzmlwj f
INNER JOIN tyyw_gg_ajjbxx aj ON f.bmsah = aj.bmsah AND aj.sfsc = 'N'

     AND aj.ajlb_bm IN (${typeRestriction})


     AND ${ajdwbm}

 WHERE f.sfsc = 'N'

      AND ${fdwbm}

      AND f.zhxgsj >= #{startDate} AND f.zhxgsj <= #{endDate}

SELECT f.wjlj || '\' || f.wjmc || f.wjhz || '.encry' filePath, f.DWBM, f.zhxgsj, f.cjsj, 'd' fileType, aj.ajlb_bm type --,f.bmsah FROM yx_dzjz_jzmlwj f INNER JOIN tyyw_gg_ajjbxx aj ON f.bmsah = aj.bmsah AND aj.sfsc = 'N'
AND aj.ajlb_bm IN (${typeRestriction})
AND ${ajdwbm}
WHERE f.sfsc = 'N'
AND ${fdwbm}
AND f.zhxgsj >= #{startDate} AND f.zhxgsj <= #{endDate} ORDER BY f.zhxgsj

select count(1) from sjfh_file_newest f
where 1=1

    AND ${fdwbm}

select id,filePath,dwbm,type,fileType,zhxgsj,cjsj,findTime from SJFH_FILE_NOFILE f
WHERE FINDTIME <= #{findTime}

    AND ${fdwbm}

  AND ROWNUM <= #{pageSize}

CREATE OR REPLACE TRIGGER file_dzjz_update
AFTER INSERT OR UPDATE ON yx_dzjz_jzmlwj
FOR EACH ROW
  /**卷宗触发器（当YX_DZJZ_JZMLWJ表中添加或者修改文书后，将会添加到sjfh_file_newest表，然后软件会处理表中数据）*/
BEGIN
  INSERT INTO sjfh_file_newest (filePath, DWBM, TYPE, fileType, ZHXGSJ, CJSJ)
    SELECT :NEW.wjlj || '\' || :NEW.wjmc || :NEW.wjhz || '.encry', :NEW.DWBM, aj.ajlb_bm, 'd', :NEW.ZHXGSJ, :NEW.CJSJ
      FROM tyyw_gg_ajjbxx aj
     WHERE aj.sfsc = 'N'

    AND ${dwbm}

    AND aj.bmsah = :NEW.BMSAH;
END;

CREATE OR REPLACE TRIGGER file_ws_update
        AFTER INSERT OR UPDATE ON YX_WS_AJJZWJ
        FOR EACH ROW
         /**电子卷宗触发器（当yx_dzjz_jzmlwj表中添加或者修改电子卷宗后，将会添加到sjfh_file_newest表，然后软件会处理表中数据）*/
      BEGIN
        INSERT INTO sjfh_file_newest (filePath, DWBM, TYPE, fileType, ZHXGSJ, CJSJ)
          SELECT :NEW.WSCFLJ, :NEW.DWBM, aj.ajlb_bm, 'w', :NEW.ZHXGSJ, :NEW.CJSJ
            FROM tyyw_gg_ajjbxx aj
           WHERE aj.sfsc = 'N'

             AND ${dwbm}

             AND aj.bmsah = :NEW.BMSAH;
      END;
